import React, { Component } from "react";
import { useHistory } from 'react-router-dom';
//import 'bootstrap/dist/css/bootstrap.css'
//import "./home.css";

class Home extends Component {
  state = {
    user: "",
    order: "",
    product: "",
  };

  userChangeHandler = (event) => {
    this.setState({ user: event.target.value });
  };

  orderChangeHandler = (event) => {
    this.setState({ order: event.target.value });
  };

  productChangeHandler = (event) => {
    this.setState({ product: event.target.value });
  };

  handleOrder = (event) => {
    console.log("Preparando la orden!");
    event.preventDefault();
    const data = this.state;
    fetch("http://127.0.0.1:5000/", {
      method: "POST",
      headers: {
        "Content-type": "application/json",
      },
      body: JSON.stringify(data),
    });
    /* .then(res => res.json())
      .then(res => console.log(res)); */
  };

  constructor() {
    let history = useHistory();
  }

  routeChange=()=> {
    let path = `/users/` + this.state.user;
    history.push(path);
  }

  render() {
    return (
      <div className="Home">
        <head>
          <meta charset="UTF-8" />
          <meta http-equiv="X-UA-Compatible" content="IE=edge" />
          <meta
            name="viewport"
            content="width=device-width, initial-scale=1.0"
          />
        </head>
        <header className="Home-header">
          {/* <link rel="stylesheet" href="./home.css" /> */}
          <title>Home Page</title>
          <h1>Bienvenido</h1>
          {/* <p>My token = {window.token}</p> */}
        </header>
        <body>
          <form class="form" onSubmit={this.handleOrder}>
            <label for="usuario">Usuario</label>
            <form action="/" method="POST">
              <input
                type="text"
                onChange={this.userChangeHandler}
                name="content"
                id="usuario"
              />
            </form>

            <label for="orden">Orden</label>
            <form action="/" method="POST">
              <input
                type="text"
                onChange={this.orderChangeHandler}
                name="content"
                id="orden"
              />
            </form>

            <label for="producto">Producto</label>
            <form action="/" method="POST">
              <input
                type="text"
                onChange={this.productChangeHandler}
                name="content"
                id="producto"
              />
            </form>

            <button>Comprar</button>
          </form>
          <input
                type="text"
                onChange={this.userChangeHandler}
                name="content"
                id="usuario"
              />
          <button onClick={this.routeChange}>Ver Compras</button>
        </body>
      </div>
    );
  }
}

export default Home;
